#!/usr/bin/env node --harmony
'use strict'
 // 定义脚手架的文件路径
process.env.NODE_PATH = __dirname + '/../node_modules/';

const program = require('commander');

 // 定义当前版本
program
    .version(require('../package').version );

// 定义使用方法
program
    .usage('<command>');

program
	.command('add')
	.description('添加模板')
    .alias('a')
	.action(function(){
		require("../command/add")();
	})
program
	.command('list')
	.description('查看模板列表')
    .alias('l')
	.action(function(){
		require("../command/list")();
	})
program
	.command('delete')
	.description('删除模板（删除模板要慎重）')
    .alias('d')
	.action(function(){
		require("../command/delete")();
	})
program
	.command('init')
	.description('初始化项目')
    .alias('i')
	.action(function(){
		require("../command/init")();
	})
program
	.command('socket')
	.description('启动react+redux调试环境')
	.option('-p, --port <string>', '端口号设置')
    .alias('s')
	.action(function(options){
		require("../command/socket")(options);
	})

program.parse(process.argv);

// 帮助文档
if(!program.args.length){
  program.help()
}