'use strict'

const co = require("co");
const prompt = require("co-prompt");
const config = require("../templates");
const chalk = require("chalk");
const fs = require("fs");
const path = require("path");

module.exports = () => {
	co(function *(){
		if(config.tpl){
			console.log(chalk.grey("模板列表："));
			for(let i in config.tpl){
				console.log(chalk.grey("-----------"));
				console.log(chalk.grey(">>模板名称："), chalk.green(i));
				console.log(chalk.grey(">>模板描述："), chalk.green(config.tpl[i].description));
				console.log(chalk.grey(">>模板地址："), chalk.green(config.tpl[i].url));
			}
		}
	})
}